<?php$name=$email=$gender=$language=$country='';$errors=['name'=>'','email'=>'','gender'=>'','language'=>'','country'=>'' ];if (!empty($_POST)) {    $name=$_POST['name'];    {        if (empty($name)) {            $errors['name']='name field is required';        }    }    $email=$_POST['email'];    {        if (empty($email)) {            $errors['email']='email field is required';        }    }    $gender=$_POST['gender'] ??'';    {        if (empty($gender)) {            $errors['gender']='gender field is required';        }    }    $language=isset($_POST['lang']) ? implode(',', $_POST['lang']) :'';    {        if (empty($language)) {            $errors['language']='language field is required';        }    }    $country=isset($_POST['country']) ? $_POST['country'] :'';    {        if (empty($country)) {            $errors['country']='country field is required';        }    }}print_r($_POST);?><!DOCTYPE html><html lang="en"><head>    <meta charset="utf-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <title>add record</title>    <style type="text/css">        body{            top: 0;            padding: 0;            margin: 0;        }        .left{            float: left;            min-height: 200px;            width: 50%;        }        .right{            float: right;            min-height: 200px;            width: 50%        }        .container{            width: 95%;            margin: auto;        }    </style></head><body><div class="container">    <div class="left">        <h1>ADD RECORD</h1>        <hr>        <form action="" method="post">            <label>Username:</label>            <input type="text" name="name" value="<?= $name?>" />            <?= $errors['name']?>            <br><br>            <label>E-mail</label>            <input type="text" name="email" value="<?= $email?>" />            <?= $errors['email']?>            <br><br>            <label>Gender:</label>            <input type="radio" <?= $gender=="male" ? "checked" :''?> name="gender" value="male">Male            <input type="radio" <?= $gender=="female" ? "checked" :''?> name="gender" value="female">Female            <?= $errors['gender']?>            <br><br>            <label>language:</label>            <input type="checkbox" name="lang[]" value="english">English            <input type="checkbox" name="lang[]" value="nepali">Nepali            <input type="checkbox" name="lang[]" value="chinese">Chinese            <?= $errors['language']?>            <br><br>            <label>Country:</label>            <select name="country">                <option disabled selected>--enter country--</option>                <option <?= $country=='nepal' ? 'selected':''?> value="nepal">Nepal</option>                <option <?= $country=='china' ? 'selected':''?> value="china">China</option>                <option <?= $country=='india' ? 'selected':''?> value="india">India</option>            </select>            <?= $errors['country']?>            <br><br>            <input type="submit" value="submit"/>        </form>    </div>    <div class="right">        <h1>SHOW RECORD</h1>        <hr>        <label>name:</label>        <?= !empty($name) ? $name : '?' ?>        <br>        <label>E-mail:</label>        <?= !empty($email) ? $email : '?' ?>        <br>        <label>Gender:</label>        <?= !empty($gender) ? $gender : '?' ?>        <br>        <label>language:</label>        <?= !empty($language) ? $language :'?' ?>        <br>        <label>Country:</label>        <?=!empty($country) ?$country :'?' ?>    </div></div></body></html>